class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> diff;
        int n = nums.size();

        for (int i = 0; i < n / 2; i++) {
            int f = nums[i];
            int s = nums[n - i - 1];
            int d = abs(f - s);
            diff.push_back(d);
        }

        unordered_map<int, int> freq;
        for (int d : diff) {
            freq[d]++;
        }

        int maxFreq = 0;
        for (const auto& p : freq) {
            maxFreq = max(maxFreq, p.second);
        }

        vector<int> candidates;
        for (const auto& p : freq) {
            if (p.second == maxFreq) {
                candidates.push_back(p.first);
            }
        }

        vector<int> temp = diff;
        sort(temp.begin(), temp.end());
        int m = temp.size();

        int mid1 = temp[m / 2];
        int mid2 = (m % 2 == 0) ? temp[m / 2 - 1] : mid1;

        candidates.push_back(mid1);
        if (mid2 != mid1) {
            candidates.push_back(mid2);
        }

        int minChanges = INT_MAX;
        for (int candidate : candidates) {
            minChanges = min(minChanges, calculateChanges(diff, nums, k, candidate));
        }

        return minChanges;
    }

private:
    int calculateChanges(const vector<int>& diff, const vector<int>& nums, int k, int target) {
        int n = nums.size();
        int ans = 0;
        int m = diff.size();

        for (int i = 0; i < m; i++) {
            int mini = min(nums[i], nums[n - i - 1]);
            int maxi = max(nums[i], nums[n - i - 1]);
            int d = diff[i];
            if (d != target) {
                if (k - mini >= target || maxi - 0 >= target) {
                    ans++;
                } else if (k - mini < target) {
                    ans += 2;
                } else if (maxi - 0 < target) {
                    ans += 2;
                } 
            }
        }
        return ans;
    }
};
