class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

      /*  int ans=0;
        if(k==0)
        {

        }*/
        vector<int>pf(k+2,0);
        int n=nums.size();
        for(int i=0;i<nums.size()/2;i++)
        {
            int a=nums[i];
            int b=(nums[n-i-1]);
            if(a>b)swap(a,b);
            int diff=abs(a-b);
            int maxi=max(a,b);
            int c=(k-min(a,b));
            maxi=max(maxi,c);
            pf[0]+=1;
            pf[diff]-=1;
            pf[diff+1]+=1;
            pf[maxi+1]+=1;
            //[0-->maxi]
        }
        int ans=1e9;
        for(int i=1;i<=k;i++)
        {
            pf[i]+=pf[i-1];
            ans=min(ans,pf[i]);
        }
        ans=min(ans,pf[0]);
        /*for(int i=0;i<pf.size();i++)
        {cout<<pf[i]<<" ";}
        cout<<endl;*/
        return ans;
    }
};
