class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> tree(k+1,0);
        map<int,int> m;

        int ans = n/2;

        for(int i=0;i<n/2;i++){
            int range = max({nums[i]-0,k-nums[i],k-nums[n-1-i],nums[n-1-i]-0});
            tree[range]++;
            int t = abs(nums[i]-nums[n-1-i]);
             m[t]++;
        }

        for(int i=k-1;i>=0;i--) tree[i] += tree[i+1];

        // for(auto x:tree)cout<<x<<" ";
        // cout<<"\n";

        for(auto x: m){
            int diff = x.first;
            int cnt = x.second;
            int needone = tree[diff];
            // cout<<diff<<" "<< needone<<"\n";
            int temp = needone-cnt + (n/2 - needone)*2; 
            ans = min(ans, temp);
        }

        return ans;
    }
};
