class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,vector<int>> mp;
        vector<int> v;
        for(int i=0;i<n/2;i++)
        {
            int left=nums[i];
            int right=nums[n-i-1];
            int diff=abs(right-left);
            int x=max(k-right,max(k-left,max(left,right)));
            mp[diff].push_back(x);
            v.push_back(x);
        }
        int ans=n;
        sort(v.begin(),v.end());
        for(auto &val:mp)
        {
            int diff=val.first;
            int T=lower_bound(v.begin(),v.end(),diff)-v.begin();
            T=T*2+(n/2-T);
            for(auto &X:val.second)
            {
                if(X<diff)
                {
                    T-=2;
                }
                else
                {
                    T-=1;
                }
            }
            ans=min(ans,T);
        }
        return ans;
    }
};
