class Solution {
public:
    int minChanges(vector<int>& a, int u) {
        map<int, vector<int>> jozzy;
        vector<int> tommy;

        for (int vipry = 0; vipry < a.size() / 2; ++vipry) {
            int hhfh = a[vipry];
            int g1 = a[a.size() - vipry - 1];
            int g2 = abs(hhfh - g1);

            int maxX = max({hhfh, g1, u - hhfh, u - g1});

            jozzy[g2].push_back(maxX);
            tommy.push_back(maxX);
        }

        int minChanges = a.size();
        sort(tommy.begin(), tommy.end());

        for (const auto& [g2, values] : jozzy) {
            int jozzyCount = lower_bound(tommy.begin(), tommy.end(), g2) - tommy.begin();
            jozzyCount = jozzyCount * 2 + (a.size() / 2 - jozzyCount);

            for (const auto& maxX : values) {
                if (maxX < g2) {
                    jozzyCount -= 2;
                } else {
                    jozzyCount -= 1;
                }
            }

            minChanges = min(minChanges, jozzyCount);
        }

        return minChanges;
    }
};
