class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,vector<int>> m;
        vector<int> v ;
        for(int i=0;i<n/2;i++)
        {
            int a=nums[i],b=nums[n-i-1];
            int d=abs(a-b);
            int x=max({a,b,k-a,k-b});
            m[d].push_back(x);
            v.push_back(x);
        }
        int ans=nums.size();
        sort(v.begin(),v.end());
        for(auto&val:m)
        {
            int d=val.first;
            int u=lower_bound(v.begin(),v.end(),d)-v.begin();
            u=u*2+(n/2-u);
            for(auto&x:val.second)
            {
                if(x<d)
                {
                    u-=2;
                }
                else
                {
                    u-=1;
                }
            }
            ans=min(ans,u);
        }
        return ans;
    }
};
