class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int, vector<int>> m;
        map<int, int> freq;
        vector<int> v;

        int n = nums.size();
        for (int i = 0; i < n / 2; i++) {
            int x = nums[i], y = nums[n - i - 1];
            int d = abs(x - y);
            int z = max({x, y, k - x, k - y});
            m[d].push_back(z);
            v.push_back(z);
            freq[z]++;
        }

        int res = n;

        for (auto& p : m) {
            int d = p.first;

            int cnt = 0;
            for (auto& entry : freq) {
                if (entry.first < d) {
                    cnt += entry.second;
                } else {
                    break;
                }
            }

            int temp = cnt * 2 + (n / 2 - cnt);

            for (auto& z : p.second) {
                if (z < d) {
                    temp -= 2;
                } else {
                    temp -= 1;
                }
            }

            res = min(res, temp);
        }

        return res;
    }
};
