class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        m = defaultdict(list)
        v = []
        n = len(nums)

        for i in range(n // 2):
            a = nums[i]
            b = nums[n - i - 1]
            diff = abs(a - b)
            X = max(a, b, k - a, k - b)
            m[diff].append(X)
            v.append(X)

        v.sort()
        ans = n

        for diff, values in m.items():
            T = bisect_left(v, diff) * 2 + (n // 2 - bisect_left(v, diff))

            for X in values:
                if X < diff:
                    T -= 2
                else:
                    T -= 1

            ans = min(ans, T)

        return ans
