class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:

        z = [0] * (k + 1)

        n = len(nums)

        d = defaultdict(int)

        for i in range(n//2):
            d[abs(nums[i] - nums[n-i-1])] += 1
            a,b =  max(nums[n-i-1], nums[i]), min(nums[n-i-1], nums[i])
            v = max(a, k - b) + 1
            if v > k:
                continue
            z[v] += 1

        for i in range(1,len(z)):
            z[i] += z[i-1]


        ans = 10 ** 9

        for each in d:
            s = n // 2 - d[each]
            ans = min(ans, (s - z[each]) + z[each] * 2  )


        return ans
