class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=size(nums),mi=INT_MAX;
        vector<int> res(k+1,0);
        unordered_map<int,int> sn;
        for(int i=0;i<n/2;i++)
        {
            sn[abs(nums[i]-nums[n-i-1])]++;
            int a=max(k-nums[i],nums[i]);
            int b=max(k-nums[n-i-1],nums[n-i-1]);
            // if(a>b){
            //     res[nums[i]]+=1;
            //     k-nums[i]>nums[i]?res[k]+=1:res[0]+=1;
            // }
            // else {
            //     res[nums[n-i-1]]+=1;
            //     k-nums[n-i-1]>nums[n-i-1]?res[k]+=1:res[0]+=1;
            // }
            res[max(a,b)]+=1;
        }
        for(int i=k-1;i>=0;i--) res[i]+=res[i+1];
        for(int i=0;i<=k;i++)
        {
             mi=min(mi,n-res[i]-sn[i]);
            //cout<<res[i]<<' '<<sn[i]<<endl;
        }
        return mi;
    }
};
