class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int>mp;
        int n = nums.size();
        for(int i=0;i<n/2;i++){
            mp[abs(nums[i] - nums[n-i-1])]++;
        }
        int maxi = INT_MIN ;
        int elemax ;
        for(auto it: mp){
            if(it.second > maxi){
                maxi = it.second;
                elemax = it.first;
            }
        }
        cout<<elemax<<endl;
        int mini = INT_MAX;
        for(auto it: mp){
            mini = min(mini , it.first);
        }
        vector<pair<int,int>>arr;
        if(mp.size() == n/2){
            arr.push_back({1 ,mini });
        }
        else{
            for(auto it: mp){
                arr.push_back({it.second , it.first});
            }
        }
        sort(arr.rbegin() , arr.rend());
        int ans = INT_MAX;
        for(auto it : arr){
            int diff = it.second;
            int curr = 0;
            for(int i=0;i<n/2;i++){
                int currDiff = abs(nums[i] - nums[n-i-1]);
                if(currDiff == diff){
                    continue;
                }
                else if( currDiff > diff){
                    curr ++ ;
                }
                else{
                    if(k - max(nums[i] , nums[n-i-1]) + min(nums[i] , nums[n-i-1]) < diff - currDiff){
                        curr = INT_MAX;
                        break;
                    }
                    else{
                        if(k-max(nums[i] , nums[n-i-1]) >= diff - currDiff || min(nums[i] , nums[n-i-1])>=diff - currDiff ){
                            curr ++ ;
                        }
                        else{
                            curr += 2;
                        }
                    }
                }
            }
            if(mp.size() == n/2){
                return curr;
            }
            if(ans < curr){
                return ans;
            }
            cout<<diff<<" "<<curr<<" "<<mp[diff]<<endl;
           ans = min(ans , curr);
        }
        return ans;
    }
};
