class Solution{
public int minChanges(int[] nums, int k) {
        Map<Integer, List<Integer>> m = new HashMap<>();
        List<Integer> list = new ArrayList<>();

        for (int i = 0; i < nums.length / 2; ++i) {
            int a = nums[i], b = nums[nums.length - i - 1];
            int diff = Math.abs(a - b);
            int X = Math.max(Math.max(a, b), Math.max(k - a, k - b));
            if (!m.containsKey(diff)) {
                m.put(diff, new ArrayList<>());
            }
            m.get(diff).add(X);
            list.add(X);
        }

        Collections.sort(list);

        int ans = nums.length;

        for (Map.Entry<Integer, List<Integer>> entry : m.entrySet()) {
            int diff = entry.getKey();
            List<Integer> values = entry.getValue();
            int temp = binarySearchCount(list, diff);
            temp = temp * 2 + (nums.length / 2 - temp);

            for (int X : values) {
                if (X < diff) {
                    temp -= 2;
                } else {
                    temp -= 1;
                }
            }

            ans = Math.min(ans, temp);
        }

        return ans;
    }

    private int binarySearchCount(List<Integer> v, int target) {
        int low = 0, high = v.size() - 1;
        while (low <= high) {
            int mid = low + (high - low) / 2;
            if (v.get(mid) < target) {
                low = mid + 1;
            } else {
                high = mid - 1;
            }
        }
        return low;
    }
}
