class Solution {
public:
    map<int,vector<int>>freq;
    int minChanges(vector<int>& nums, int k) {

        vector<int> v;
        int n = nums.size();
        for(int i = 0;i<n/2;i++){
            int d = abs(nums[i]-nums[n-i-1]);
            freq[d].push_back(max(max(nums[i], k - nums[i]), max(nums[n-i-1], k - nums[n-i-1])));
            v.push_back(max(max(nums[i], k - nums[i]), max(nums[n-i-1], k - nums[n-i-1])));
        }
        int res = n;
        sort(v.begin(),v.end());

        for(auto f : freq){

            int d = f.first;
            int tmp = lower_bound(v.begin(),v.end(),d)-v.begin();
            tmp += n / 2;

            for(auto it:f.second){
                if(it<d){
                    tmp-=2;
                }
                else{
                    tmp--;
                }
            }

            res = min(res,tmp);

        }

        return res;
    }
};
