class Solution {
public:
    typedef pair<int,int> PII;
    static bool cmp(PII a,PII b){
        if(a.first==b.first){
            return a.second<b.second;
        }
        else 
            return a.first>b.first;
    }
    int calc(int maxnum,vector<int>& nums, int k,int n){
        int ans=0;
        for(int i=0;i<nums.size()/2;i++){
            int num=abs(nums[i]-nums[n-i-1]);
            int small=min(nums[i],nums[n-i-1]),big=max(nums[i],nums[n-i-1]);
            if(num==maxnum) continue;
            if(big-maxnum<=k && big-maxnum>=0){
                ans+=1;
            }
            else if(small+maxnum<=k && small+maxnum>=0){
                ans+=1;
            }
            else  {
                ans+=2;
            }
        }
        return ans;
    }
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        map<int,int> m;
        int maxnum;
        for(int i=0;i<nums.size()/2;i++){
            int num=abs(nums[i]-nums[n-i-1]);
            if(num<=k){
                m[num]++;
            }
        }    
        vector<PII> v;
        for(auto x:m){
            v.push_back({x.second,x.first});
        }
        sort(v.begin(),v.end(),cmp);
        //maxnum=v[0].second;
        //cout<<"maxnum: "<<maxnum<<endl;
        int ans=INT_MAX;
        for(int i=0;i<min(5,int(v.size()));i++){
            maxnum=v[i].second;

            ans=min(ans,calc(maxnum,nums,k,n));
        }
        return ans;
    }
};
