class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        freq = {}
        n = len(nums)

        for i in range(n//2):
            x = abs(nums[i] - nums[n-i-1])
            if x in freq:
                freq[x] += 1
            else:
                freq[x] = 1

        minAns = n

        for dif, frequency in sorted(freq.items(), key = lambda x: x[1], reverse = True)[:5]:
            ans = 0
            for i in range(n//2):
                x = abs(nums[i] - nums[n-i-1])
                if x != dif:
                    if 0 <= nums[i] - dif <= k \
                    or 0 <= nums[n-i-1] - dif <= k \
                    or nums[i] + dif <= k \
                    or nums[n-i-1] + dif <= k:
                        ans += 1
                    else:
                        ans += 2

                if ans>=minAns:
                    break
            # print(dif, ans, minAns)
            if ans < minAns:
                minAns = ans

        return minAns
