class Solution {
public:
    // int a[100010];
    unordered_map<int, int > mp;
    vector<pair<int,int> > arr;
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size() ;
        for(int i = 0; i < n / 2; i ++){
            int xs = abs(nums[i] - nums[n - i - 1]);
            // cout << a[i] << ' ' << endl;
            mp[xs] ++;
        }
        for(auto x: mp){
            arr.push_back(x);
        }
        sort(arr.begin(), arr.end(), [](pair<int, int> a, pair<int, int> b){
            return a.second > b.second;
        });
        // for(auto x : arr){
        //     cout << x.first << ' ' << x.second << endl;
        // }
        int sum = n / 2;
        int ans = sum - 1, cnt = 0;
        for(auto x : arr){
            int as = 0;
            int fr = x.first;
            for(int i = 0; i < n / 2; i ++){
                // int xf = 0;
                // cout << nums[i] << endl;
                int xf = abs(nums[i] - nums[n - i - 1]);
                // cout << fr << ' ' << nums[i] << ' ' << nums[n - i - 1] << endl;

                if(xf == x.first){
                    continue;
                }else if(nums[i] + fr <= k || nums[i] - fr >= 0 || nums[n - i - 1] - fr >= 0 || nums[n - i - 1] + fr <= k){
                // cout << "  " << fr << ' ' << nums[i] << ' ' << nums[n - i - 1] << endl;

                    as += 1;
                }else{
                    as += 2;
                }
            }
            // cout << endl;
            ans = min(ans, as);
            if(cnt > 40) break;
            cnt ++;
            // cout << ans << ' ' << sum - x.second << endl;
            // ans = min(ans,  sum - x.second);
        }
        return ans;

    }
};
