class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> m;
        vector<int> v;

        for (int i = 0; i < nums.size() / 2; ++i) {
            int a = nums[i];
            int b = nums[nums.size() - i - 1];
            int diff = abs(a - b);

             int X = max({a, b, k - a, k - b});

             m[diff].push_back(X);
            v.push_back(X);
        }

        int ans = nums.size();  
        sort(v.begin(), v.end());

        for (const auto& [diff, values] : m) {
            int g = lower_bound(v.begin(), v.end(), diff) - v.begin();
            g = g * 2 + (nums.size() / 2 - g);

            for (const auto& X : values) {
                if (X < diff) {
                    g -= 2;
                } else {
                    g -= 1;
                }
            }

            ans = min(ans, g);
        }

        return ans;
    }
};
