class Solution {
   private int binarySearchCalculate(List<Integer> list, int difference) {
        int left = 0;
        int right = list.size();
        while (left < right) {
            int mid = left + (right - left) / 2;
            if (list.get(mid) < difference) {
                left = mid + 1;
            } else {
                right = mid;
            }
        }
        return left;
    }

    public int minChanges(int[] nums, int k) {
        Map<Integer, List<Integer>> map = new HashMap<>();
        List<Integer> list = new ArrayList<>();

        int n = nums.length;


        for (int i = 0; i < n / 2; i++) {
            int a = nums[i];
            int b = nums[n - i - 1];
            int diff = Math.abs(a - b);
            int X = Math.max(Math.max(a, b), Math.max(k - a, k - b));

            map.putIfAbsent(diff, new ArrayList<>());
            map.get(diff).add(X);
            list.add(X);
        }


        Collections.sort(list);

        int ans = n;


        for (Map.Entry<Integer, List<Integer>> entry : map.entrySet()) {
            int diff = entry.getKey();
            List<Integer> XList = entry.getValue();


            int idx = binarySearchCalculate(list, diff);


            int T = 2 * idx + (n / 2 - idx);


            for (int X : XList) {
                if (X < diff) {
                    T -= 2;
                } else {
                    T -= 1;
                }
            }


            ans = Math.min(ans, T);
        }

        return ans;
    }
}
