class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        x, li = dict(), []
        for i in range(len(nums) // 2):
            if abs(nums[i] - nums[-i-1]) in x:
                x[abs(nums[i] - nums[-i-1])] += 1
            else:
                x[abs(nums[i] - nums[-i-1])] = 1
            li.append(max(k - nums[i], nums[i], k - nums[-i-1], nums[-i-1]))
        li.sort()
        frq = []
        for i in x:
            frq.append((x[i], i))
        frq.sort()
        arr = li
        ans = len(arr)
        while frq:
            f, ele = frq.pop()
            left, right = 0, len(arr) - 1
            result = len(arr)  
            target = ele
            while left <= right:
                mid = (left + right) // 2
                if arr[mid] < target:
                    left = mid + 1
                else:
                    right = mid - 1
                    if arr[mid] == target:
                        result = mid  

            i = result if result != len(arr) else left
            ans = min(ans, i * 2 + len(li) - f - i)
        return ans
