class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        unordered_map<int,int> m;
        int n=a.size();
        int maxi=0;
        for(int i=0;i<a.size()/2;i++)
        {
            // if(a[i]==k/2 && )
            m[abs(a[i]-a[n-i-1])]++;
            // maxi=max(maxi,m[abs(a[i]-a[n-i-1])]);
        }
        vector<pair<int,int>> v;
        for(auto it:m)
        {
            v.push_back({it.second,it.first});
        }
        sort(v.rbegin(),v.rend());

        // for(auto it:v)
        // cout<<it.first<<" "<<it.second<<endl;
        int ans=INT_MAX;
        int curr=INT_MAX;
        for(int i=0;i<v.size() && curr<=ans;i++)
        {
            curr=0;
            int cd=v[i].second;
            // cout<<"new value of CD "<<endl;
            // cout<<"Value of CD now is "<<cd<<endl;
            for(int j=0;j<n/2;j++)
            {
                if(abs(a[j]-a[n-j-1])==cd)
                continue;
                if(max(a[j],a[n-j-1])-0<cd && k-min(a[j],a[n-j-1])<cd)
                {
                    curr+=2;
                }
                else {
                    curr++;
                }
            }

            ans=min(ans,curr);
        }
        return ans;
    }
};
