class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        auto len = nums.size();
        vector<int> up(len/2, 0);
        vector<int> subs(len/2, 0);
        unordered_map<int, int> mp;
        for(auto i = 0; i < len/2; ++i) {
            int a = nums[i];
            int b = nums[len-i-1];
            int sub = abs(a - b);
            mp[sub]++;
            subs[i] = sub;
            up[i] = max({a, b, k-a, k-b});
        }

        vector<pair<int, int>> vt;
        for (auto &it: mp) {
            vt.emplace_back(it.first, it.second);
        }
        std::sort(vt.begin(), vt.end(), [](const pair<int, int>& a, const pair<int, int>& b) {
            return a.second > b.second;
        });

        int res = INT_MAX;
        for (auto &it: vt) {
            int sum = 0;

            bool flag  = true;
            int sub = it.first;
            for (auto i = 0; i < len/2; ++i){
                if (subs[i] != sub) {
                    if (sub > up[i]) {
                        flag = false;
                        sum+=2;
                    } else {
                        sum++;
                    }
                }
            }

            res = min(sum, res);
            if (flag) break;
        }

        return res;
    }
};
