/**
 * @param {number[]} nums
 * @param {number} k
 * @return {number}
 */
var minChanges = function (nums, k) {
  let len = nums.length;
  let n = nums.length / 2;
  let count = n * 2;
  let diffList = new Array(k + 1).fill(0);
  let map = {};
  for (let i = 0; i < n; i++) {
    const key = Math.abs(nums[i] - nums[len - 1 - i]);
    map[key] ? map[key]++ : (map[key] = 1);
    let min = k - Math.min(nums[i], nums[len - 1 - i]);
    let max = Math.max(nums[i], nums[len - 1 - i]);
    let temp = Math.max(min, max);
    diffList[temp]++;
  }
  let list = [0];
  for (let i = Math.ceil(k / 2); i < diffList.length - 1; i++) {
    list.push(list.at(-1) + diffList[i]);
  }
  for (let key in map) {
    let t = map[key];
    if (key <= Math.ceil(k / 2)) {
      count = Math.min(n - t, count);
    } else {
      let index = key - Math.ceil(k / 2);
      let t = map[key];
      let p = n - t + list[index];
      count = Math.min(p, count);
    }
  }
  return count;
};
