from collections import Counter

class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        counter = Counter()
        for i in range(n // 2):
            counter[abs(nums[i] - nums[n - i - 1])] += 1

        res = 1e5
        for max_ele, val in counter.most_common(10):
            ans = 0
            for i in range(n // 2):
                ab = abs(nums[i] - nums[n - i - 1])
                if ab != max_ele:
                    ans += 1
                    if  k - min(nums[i], nums[n - i - 1]) < max_ele and max(nums[i], nums[n - i - 1]) < max_ele:
                        ans += 1
            res = min(res, ans)

        return res
