class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> vec;
        map<int,vector<int>> map_use;        
        for(int i = 0;i<nums.size()/2;i++){
            int a = nums[i],b = nums[nums.size()-i-1];
            int diff = abs(a-b);
            int X = max({a,b,k-a,k-b});
            map_use[diff].push_back(X);
            vec.push_back(X);
        }
        int ans = nums.size();
        sort(vec.begin(),vec.end());
        for(auto &val:map_use){
            int diff = val.first;
            int T = lower_bound(vec.begin(),vec.end(),diff)-vec.begin();
            T = T*2+(nums.size()/2-T);
            for(auto &X:val.second){
                if(X<diff){
                    T-=2;
                }
                else{
                    T-=1;
                }
            }

            ans = min(ans,T);

        }

        return ans;

    }
};
