class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> v;
        map<int,vector<int>> m;
        int n = nums.size();

        for(int i = 0;i<n/2;i++){
            int a = nums[i];
            int b = nums[nums.size()-i-1];
            int dif = abs(a-b);
            int temp = max({a,b,k-a,k-b});
            v.push_back(temp);
            m[dif].push_back(temp);  
        }
        sort(v.begin(),v.end());
        int ans = n;
        for(auto it:m){
            int dif = it.first;
            int temp = lower_bound(v.begin(),v.end(),dif)-v.begin();
            temp = temp*2+(nums.size()/2-temp);
            for(auto val:it.second){
                if(val<dif) temp-=2;
                else temp-=1;  
            }
            ans = min(ans,temp);
        }
        return ans;
    }
};
