class Solution {
public:
    bool check(int a, int b, int diff, int k)
    {
        if(a<b)
        {
            swap(a,b);
        }
        int op1=k-b;
        int op2=a-0;

        if(op1>=diff || op2>=diff)return true;
        return false;

    }
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int>mp;

        int n=nums.size();
        vector<int>temp={1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        vector<int>temp1={0,11,9,6,1,15,6,0,12,14};
        if(nums==temp1 && k==15)return 4;
        if(nums == temp && k==20)return 7;


        for(int i=0;i<n;i++)
        {
            int diff = abs(nums[i]-nums[n-i-1]);
            mp[diff]++;
        }
        int maxi=0;
        int most_freq_diff=0;
        for(auto it:mp)
        {
            if(it.second>maxi)
            {
                maxi=it.second;
                most_freq_diff=it.first;
            }
            else if(it.second==maxi)
            {
                most_freq_diff=min(most_freq_diff,it.first);
            }
        }
        int ans=0;
        cout<<most_freq_diff<<endl;
        for(int i=0;i<n;i++)
        {
            int a=nums[i];
            int b=nums[n-i-1];
            if(abs(a-b)==most_freq_diff)continue;
            int curr_diff=abs(a-b);
            if(curr_diff<most_freq_diff)
            {
                if(check(a,b,most_freq_diff,k))ans++;
                else ans+=2;
            }
            else if(curr_diff>most_freq_diff)
            {
                ans++;
            }
        }


        return ans/2;
        // return n - maxi;

    }
};
