class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        money=defaultdict(list)
        v=[]
        for i in range(len(nums) // 2):
            a = nums[i]
            b = nums[len(nums) - i - 1]
            kashish = abs(a - b)

            X = max(a, b, k - a, k - b)

            money[kashish].append(X)
            v.append(X)

        ans = len(nums)
        v.sort()

        for kashish, values in money.items():
            T = bisect_left(v, kashish)
            T = T * 2 + (len(nums) // 2 - T)

            for X in values:
                if X < kashish:
                    T -= 2
                else:
                    T -= 1

            ans = min(ans, T)

        return ans
