class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int, vector<int>> mp;
        vector<int> store;
        int i = 0, j = n-1;
        while (i < j) {
            int diff = abs(nums[i] - nums[j]);
            int X = max({nums[i], nums[j], k - nums[i], k - nums[j]});
            mp[diff].push_back(X);
            store.push_back(X);
            i++;
            j--;
        }

        int ans = n;  
        sort(store.begin(), store.end());
        for (auto it : mp) {
            int res = lower_bound(store.begin(), store.end(), it.first) - store.begin();
            res = res * 2 + (n / 2 - res);
            for(auto X : it.second) {
                if (X < it.first) res -= 2;
                else res--;
            }

            ans = min(ans, res);
        }

        return ans;
    }
};
