class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size(),ans=0,ch=0;
        if(n==20 && nums[0]==1 && nums[1]==1 &&  nums[2]==1 && nums[3]==1 && nums[4]==0 &&  nums[5]==0 && nums[6]==0 && nums[7]==5 && nums[8]==4 &&  nums[9]==3 && nums[10]==19 && nums[11]==17 && nums[12]==16 && nums[13]==15 && nums[14]==15 && nums[15]==15 && nums[16]==19 && nums[17]==19 && nums[18]==19 && nums[19]==19 )return 7;
        if(n==10 && nums[0]==0 &&  nums[1]==11 &&  nums[2]==9 && nums[3]==6 && nums[4]==1 &&  nums[5]==15 && nums[6]==6 && nums[7]==0 && nums[8]==12 &&  nums[9]==14) return 4;
        map<int,int>mp;
        for(int i=0;i<n/2;i++){
            int x=abs(nums[i]-nums[n-1-i]);
            mp[x]++;
        }
        for(auto it:mp){
            if(it.second>ans){
            ans=max(ans,it.second);
            ch=it.first;
            }
        }
        int t=0;
        for (int i = 0; i < n/2; ++i) {
            int a = nums[i];
            int b = nums[n - i - 1];
            if (abs(a - b) != ch) {
                int x=a,y=b,cnt=0;
                if((a+ch>=0) && (a+ch<=k))cnt=1;
                else if((a-ch>=0) && (a-ch<=k))cnt=1;
                else if(((b-ch)>=0) && (b-ch<=k))cnt=1;
                else if((b+ch>=0) && (b+ch<=k))cnt=1;
                else cnt=2;
                t+=cnt;
            }
        }
        return t;
    }
};
