class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> L;
        vector<int> D;
        for(int i = 0;i<nums.size()/2;i++){
            int a = nums[i],b = nums[nums.size()-i-1];
            int difr = abs(a-b);
            int G = max({a,b,k-a,k-b});
            L[difr].push_back(G);
            D.push_back(G);
        }
        int ans = nums.size();
        sort(D.begin(),D.end());

        for(auto &val:L){

            int difr = val.first;
            int Q = lower_bound(D.begin(),D.end(),difr)-D.begin();
            Q = Q*2+(nums.size()/2-Q);

            for(auto &G:val.second){
                if(G<difr){
                    Q-=2;
                }
                else{
                    Q-=1;
                }
            }

            ans = min(ans,Q);

        }

        return ans;

    }
};
