class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int, int> mp;
        vector<int> diff(k + 2, 0);
        for (int i = 0; i < n / 2; i++) {
            if (nums[i] > nums[n - 1 - i]) {
                swap(nums[i], nums[n - 1 - i]);
            }
            mp[nums[n - 1 - i] - nums[i]]++;
            int tmp = max(k - nums[i], nums[n - 1 - i]);
            diff[tmp + 1]++;
        }
        int ans = INT_MAX;
        for (int i = 1; i <= k + 1; i++)
            diff[i] += diff[i - 1];
        for (auto& [k, v] : mp) {
            ans = min(ans, n / 2 - v + diff[k]);
        }
        return ans;
    }
};
