class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> c(n >> 1);
        unordered_map<int, int> mp;
        for(int i = 0; i < n / 2; i++) { 
            int l = nums[i], r = nums[n - i - 1];
            if(l > r) swap(l, r);
            c[i] = max(k - l, r);
            int x = r - l;
            mp[x] ++;
        }
        sort(c.begin(), c.end());
        int res = 0x3f3f3f3f;
        for(int i = 0; i <= k; i++) {
            int pos = lower_bound(c.begin(), c.end(), i) - c.begin();
            res = min(res, pos * 2 + (n / 2 - pos)  - mp[i]);
        }
        return res;
    }
};
