class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
       unordered_map<int,vector<int>>mp;
        vector<int>x;
        for(int i = 0;i<nums.size()/2;i++){
            int m = nums[i],n = nums[nums.size()-i-1];
            int di = abs(m-n);
            int X = max({m,n,k-m,k-n});
            mp[di].push_back(X);
            x.push_back(X);
        }
        int cal = nums.size();
        sort(x.begin(),x.end());

        for(auto hue:mp){

            int diff = hue.first;
            int T = lower_bound(x.begin(),x.end(),diff)-x.begin();
            T = T*2+(nums.size()/2-T);

            for(auto X:hue.second){
                if(X<diff){
                    T-=2;
                }
                else{
                    T-=1;
                }
            }

            cal = min(cal,T);

        }

        return cal;

    }
};
