#include <bits/stdc++.h>

#define FOR(i, n) for (int i = 0; i < (n); ++i)
#define PB push_back
#define ABS(x) std::abs(x)
#define MAX4(a, b, c, d) std::max({a, b, c, d})
#define SORT(v) std::sort((v).begin(), (v).end())
#define MIN(a, b) std::min(a, b)
#define LB(v, x) std::lower_bound((v).begin(), (v).end(), x) - (v).begin()

class Solution
{
public:
    int minChanges(std::vector<int> &nums, int k)
    {
        std::map<int, std::vector<int>> mp;
        std::vector<int> vals;

        int n = nums.size();

        FOR(i, n / 2)
        {
            int l = nums[i];
            int r = nums[n - i - 1];
            int diff = ABS(l - r);
            int maxDiff = MAX4(l, r, k - l, k - r);

            mp[diff].PB(maxDiff);
            vals.PB(maxDiff);
        }

        SORT(vals);

        int mini = n;

        for (const auto &[diff, values] : mp)
        {
            int T = LB(vals, diff);
            T = T * 2 + (n / 2 - T);

            for (const auto &v : values)
            {
                if (v < diff)
                {
                    T -= 2;
                }
                else
                {
                    T -= 1;
                }
            }

            mini = MIN(mini, T);
        }

        return mini;
    }
};
