class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        unordered_map<int, int> count;
        vector<int> max_v;
        for(int i = 0; i < n / 2; ++i){
            int diff = abs(nums[n - 1 - i] - nums[i]);
            int a = min(nums[n - 1 - i], nums[i]);
            int b = max(nums[n - 1 - i], nums[i]);
            count[diff]++;
            max_v.push_back(max(b, k - a));
        }

        sort(max_v.begin(), max_v.end());

        int ans = INT32_MAX;
        for(int i = 0; i <= k; ++i){
            int p = max_v.end() - lower_bound(max_v.begin(), max_v.end(), i);
            int cost = p - count[i] + (n / 2 - p) * 2;
            ans = min(ans, cost);
        }

        return ans;
    }
};
