class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        int dp[k+1];
        int cnt[k+1];
        memset(dp,0,sizeof(dp));
        memset(cnt,0,sizeof(cnt));
        int i=0;
        int j=n-1;
        while(i<j){
            int val=abs(nums[j]-nums[i]);
            int maxi=max(nums[i],nums[j]);
            int mini=min(nums[i],nums[j]);
            int maxk=max(maxi-0,k-mini);
            dp[0]+=1;
            if(maxk+1<=k){
              dp[maxk+1]+=1;
            }
            cnt[val]++;
            i++;
            j--;
        }
        for(int i=1;i<=k;i++){
            dp[i]+=dp[i-1];
        }
        for(int i=0;i<=k;i++){
            dp[i]=dp[i]-cnt[i];
        }
        int x=-1;
        int mini=1e9;
        for(int i=0;i<=k;i++){
           if(dp[i]<=mini){
              mini=dp[i];
              x=i;
           }
        }
        i=0;
        j=n-1;
        int ans=0;
        while(i<j){
            int val=abs(nums[j]-nums[i]);
            int maxi=max(nums[i],nums[j]);
            int mini=min(nums[i],nums[j]);
            int maxk=max(maxi-0,k-mini);
            if(abs(val)!=x){
                ans++;
                if(maxk<x){
                   ans++;
                }
            }
            i++;
            j--;
        }
        return ans;
    }
};
