class Solution:
    def __getMaxKeyVal(self, d: dict) -> Tuple[int, int]:
        maxKey = -1
        maxVal = -1
        for key, value in d.items():
            if maxVal == -1 or value > maxVal or (value == maxVal and key < maxKey):
                maxKey = key
                maxVal = value
        return maxKey, maxVal
    def minChanges(self, nums: List[int], k: int) -> int:
        # abs(nums[i] - nums[j]) < k
        freq = defaultdict(int)
        n = len(nums)
        for i in range(n):
            r = n - i - 1
            if r <= i:
                break
            freq[abs(nums[r] - nums[i])] += 1
        # maxKey, maxFreq = self.__getMaxKeyVal(freq)
        # print(maxKey, maxFreq)

        ans = 2e5
        freqList = sorted(list(freq.items()), key = lambda x: x[1], reverse = True)
        for key, value in freqList[: min(3, len(freqList))]:
            op = 0
            for i in range(n):
                if op > ans:
                    break
                r = n - i - 1
                if r <= i:
                    break
                if abs(nums[r] - nums[i]) == key:
                    continue
                if (
                    nums[i] + key <= k or 
                    nums[i] - key >= 0 or
                    nums[r] + key <= k or 
                    nums[r] - key >= 0 
                ):
                    op += 1
                else:
                    op += 2
                # print(f"nums[{r}] - nums[{i}] = {nums[r] - nums[i]}, i = {i}, op = {op}")
            ans = min(ans, op)
        return ans
