class Solution {
public:
    int minChanges(vector<int>& nums, int k)
    {
        int i,j,s;
        s=nums.size();
        unordered_map<int,int> m;
        int d;
        for(i=0;i<s/2;i++)
        {
            d=abs(nums[i]-nums[s-i-1]);
            m[d]++;
        }
        int ma=-1;
        int mad=-1;
        vector<pair<int,int>> v;
        for(auto &pr:m)
        {
            v.push_back(make_pair(pr.second,pr.first));
        }
        sort(v.begin(),v.end());
        int ans=0;
        int dd;
        // cout<<m[5]<<endl;
        // cout<<m[3]<<endl;
        // cout<<"mad="<<mad;
        // for(i=0;i<v.size();i++)
        // {
        //     cout<<"first="<<v[i].first<<endl;
        //     cout<<"second="<<v[i].second<<endl;
        // }
        for(j=v.size()-1;j>=0;j--)
        {
            mad=v[j].second;
            ans=0;
            for(i=0;i<s/2;i++)
            {
                d=abs(nums[i]-nums[s-i-1]);
                if(d!=mad)
                {
                    dd=abs(nums[i]-d);
                    if(max(nums[i],abs(nums[i]-k))>=mad)
                    {
                        ans++;
                        continue;
                    }
                    // else if(abs(nums[i]+k))
                    // {
                    //     ans++;
                    //     continue;
                    // }
                    else
                    {
                        dd=abs(nums[s-1-i]-d);
                        if(max(nums[s-i-1],abs(nums[s-1-i]-k))>=mad)
                        {
                            ans++;
                            continue;
                        }
                        // else if(nums[s-1-i]-k<=nums[s-1-i]-mad)
                        // {
                        //     ans++;
                        //     continue;
                        // }
                        else
                        ans=ans+2;
                    }
                }
            }
            if(j!=0)
            {
                if(ans<=(s/2) -v[j-1].first)
                return ans;
            }
        }
        return ans;
    }
};
