#include<ext/pb_ds/assoc_container.hpp>
#include<ext/pb_ds/tree_policy.hpp>
using namespace __gnu_pbds;
template<class T> using ordered_set = tree<T, null_type,less_equal<T>,rb_tree_tag,tree_order_statistics_node_update>;
class Solution {
public:
    int minChanges(vector<int>& arr, int k) {
    int n = arr.size(), minKharcha = n;
    vector<vector<int>> v;
    map<int, vector<int>> freq;

    for(int i = 0; i < n/2; i++) {
        int dif = abs(arr[i] - arr[n - i - 1]);
        int mi = min(arr[i], arr[n - i - 1]), mx = max(arr[i], arr[n - i - 1]);
        freq[dif].push_back(max(mx, k - mi));
    }

    ordered_set<int> st;
    int badeLog = n/2, choteLog = 0;

    for(auto &e: freq) {
        int curDif = e.second.size();
        choteLog = st.size();
        badeLog = n/2 - curDif - choteLog;

        // deb(e.first, e.second, badeLog, choteLog);
        int doOperationWale = st.order_of_key(e.first);

        int curCost = badeLog + doOperationWale + (choteLog);
        minKharcha = min(minKharcha, curCost);

        for(auto &x: e.second) {
            st.insert(x);
        }
    }
    return minKharcha;

}
};
