class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
       int n =nums.size();
       map<int,vector<int>>mp;
       vector<int>v;
       for(int i=0;i<n/2;i++){
           int x=nums[i],y=nums[n-i-1];
           int dif=abs(x-y);
           int mx=max({x,y,k-x,k-y});
           mp[dif].push_back(mx);
           v.push_back(mx);
       }
       int res=nums.size();
       sort(v.begin(),v.end());
       for(auto &[dif,z]:mp){
        int temp=lower_bound(v.begin(),v.end(),dif)-v.begin();
        temp=temp*2+(n/2-temp);
        for(auto &c:z){
            if(c<dif){
                temp-=2;
            }
            else{
                temp-=1;
            }
        }
        res=min(res,temp);
       }
       return res;
    }
};
