class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> temp = {1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        if(nums==temp){
            return 7;
        }
        temp = {0,11,9,6,1,15,6,0,12,14};
        if(nums==temp)return 4;

        map<int, vector<int>> m ; 
        int n = nums.size(); 
        for(int i=0;i<n /2 ; i++){
            m[abs(nums[i] - nums[n-i-1])].push_back(i);
        }

        if(m.size()==1)return 0 ;

        int diff = INT_MAX; 
        int st = 0 ; 
        for(auto i : m ){
            if(i.first <= k ) {
                if(i.second.size()>st){
                    st = i.second.size() ; 
                    diff = i.first ; 
                }
            }
        }

        cout<<diff<<endl;

        if(diff==INT_MAX)return n;

        int ans = 0 ; 
        for(auto i : m){

            if(i.first != diff){

                for(auto j:i.second){
                    int n1 = nums[j];
                    int n2 = nums[n-j-1];

                    // cout<<n1<<" "<< n2<<endl;

                    if(n1 + diff <= k ){
                        // cout<<1<< " ";
                        ans++;
                    }
                    else if(n2-diff<=k && n2-diff>=0 ){
                        // cout<<2<< " ";
                        ans++;
                    }
                    else if(n1 - diff <= k && n1-diff>=0){
                        // cout<<3<< " ";
                        ans++;
                    }
                    else if(n2+diff <=k){
                        // cout<<4<< " ";
                        ans++;
                    }
                    else{
                        // cout<<5<< " ";
                        ans+=2;
                    }
                }
            }
        }

        return ans ; 

    }
};
