class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int, vector<int>> mpp;
        vector<int> haha;

        for (int i = 0; i < n / 2; i++) { 
            int x = max({nums[i], nums[n - i - 1], k - nums[i], k - nums[n - i - 1]});
            mpp[abs(nums[i] - nums[n - i - 1])].push_back(x);
            haha.push_back(x);
        }

        sort(haha.begin(), haha.end());
        int ans = n;

        for (auto it : mpp) {
            int diff = it.first;
            int wot = lower_bound(haha.begin(), haha.end(), diff) - haha.begin();
            wot = wot * 2 + (n / 2 - wot);
            for (auto it2 : it.second) {
                if (it2 < diff) wot -= 2;
                else wot -= 1;
            }
            ans = min(ans, wot);
        }

        return ans;
    }
};
