class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int> mx;
        map<int,int> mp;
        for(int i=0; i<n/2; i++){
            int a= max(nums[i], nums[n-1-i]);
            int b= min(nums[i], nums[n-1-i]);
            mx.push_back( max(a, k-b) );
            mp[abs(nums[i]- nums[n-1-i])]++;
        }
        sort(mx.begin(), mx.end());

        // for(auto x: mx) cout<<x<<' '; cout<<endl;

        // for(auto x: nums) mp[x]++;

        int ans=n;
        for(int x=0; x<=k; x++){
            auto it= lower_bound(mx.begin(), mx.end(), x)- mx.begin();
            // it--;
            int temp= (it)*2;

            temp+= (n/2-it);
            temp-= mp[x];
            // cout<<x<<"-"<<temp<<endl;
            ans= min(ans, temp);
        }

        return ans;
    }
};
