class Solution {
public:
    int minChanges(std::vector<int>& nums, int k) {
        unordered_map<int,vector<int>>mp;
        vector<int> v;

        for (int i = 0; i < nums.size() / 2; ++i) {
            int a1 = nums[i];
            int a2 = nums[nums.size() - i - 1];
            int diff = abs(a1 - a2);

             int val = max({a1,a2, k - a1, k - a2});

             mp[diff].push_back(val);
            v.push_back(val);
        }

        int ans = nums.size();  
        sort(v.begin(), v.end());

        for (const auto& [diff, values] : mp) {
            int temp =lower_bound(v.begin(), v.end(), diff) - v.begin();
            temp= temp * 2 + (nums.size() / 2 - temp);

            for (const auto& X : values) {
                if (X < diff) {
                    temp -= 2;
                } else {
                    temp -= 1;
                }
            }

            ans = min(ans,temp);
        }

        return ans;
    }
};
