class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:

        ht = {}
        n = len(nums)
        pre = [0 for i in range(k+2)]
        for i in range(len(nums)//2):
            diff = abs(nums[i] - nums[n-i-1])

            if diff not in ht:
                ht[diff] = 1
            else:
                ht[diff] += 1

            max_diff = max(abs(k - nums[i]) , abs(k - nums[n-i-1]), abs(nums[i]), abs(nums[n-i-1]))
            pre[0] += 1
            pre[max_diff+1] += 1

        check = sorted(ht.items(), key=lambda item: item[1], reverse=True)

        count = 0
        for i in range(len(pre)):

            count += pre[i]
            pre[i] = count

        #print(pre)

        if(check[0][1] == n//2):
            return 0

        ans = 1000000000
        #print(check)
        for c in check:

            d = c[0]
            ans = min(ans, pre[d]-c[1])






        return ans
