public class Solution {
    public struct Sat{
        public int Diff = 0;
        public int Count = 0;

        public Sat(int diff, int count){
            Diff = diff;
            Count = count;
        }
    }

    public int MinChanges(int[] nums, int k) {
        Dictionary<int, int> dict = new Dictionary<int, int>();
        int[] difflookup = new int[nums.Length];
        for (int i = 0; i < nums.Length / 2; i++){
            int diff = Math.Abs(nums[i] - nums[nums.Length - 1 - i]);
            if (dict.ContainsKey(diff)){
                dict[diff]++;
            }
            else{
                dict[diff] = 1;
            }
            difflookup[i] = diff;
        }

        List<Sat> l = new List<Sat>();
        foreach(var pair in dict){
            l.Add(new Sat(pair.Key, pair.Value));
        }
        l.Sort((a,b) => a.Count - b.Count);

        int retval = FindChangeCount(nums, difflookup, k, 
        l[l.Count - 1].Diff);
        int pairNum = nums.Length / 2;

        for (int i = l.Count - 2; i >= 0; i--){
            if ((pairNum - l[i].Count) > retval){
                break;
            }
            int c = FindChangeCount(nums, difflookup, k, l[i].Diff);
            retval = Math.Min(retval, c);
        }
        return retval;
    }

    int FindChangeCount(int[] array, int[] difflookup, int k, int targetDiff){
        int mod = 0;
        for (int i = 0; i < array.Length / 2; i++){
            if(difflookup[i] == targetDiff){
                continue;
            }
            int min;
            int max;
            if (array[i] < array[array.Length - 1 - i]){
                min = array[i];
                max = array[array.Length - 1 - i];
            }
            else{
                max = array[i];
                min = array[array.Length - 1 - i];
            }

            if(k - min < targetDiff && max < targetDiff){
                mod+=2;
            }
            else{
                mod+=1;
            }
        }
        return mod;
    }
}
