class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int>tmp;
        int n=nums.size();
        int ans=n;
        map<int,vector<int>>mpp;
        for(int i=0;i<n/2;i++){
            int d=abs(nums[i]-nums[n-i-1]);
            int maxi=max(nums[i],nums[n-i-1]);
            maxi=max(maxi,max(k-nums[i],k-nums[n-i-1]));
            mpp[d].push_back(maxi);
            tmp.push_back(maxi);
        }
        sort(tmp.begin(),tmp.end());
        for(auto it:mpp){
            int d=it.first;
            int count=lower_bound(tmp.begin(),tmp.end(),d)-tmp.begin();
            count+=n/2;
            for(auto num:it.second){
                if(num>=d)count--;
                else count-=2;
            }
            ans=min(ans,count);
        }
        return ans;

    }
};
