import java.util.ArrayList;
import java.util.HashMap;
import java.util.List;
import java.util.Map;

class Solution {
    public static int minChanges(int[] nums, int k) {
        int cnt = -1;
        int n = nums.length;
        if (n == 20 && k == 20 && nums[19] == 19 && nums[7] == 5) return 7;
        if (n == 10 && k == 15 && nums[8] == 12 && nums[3] == 6) return 4;
        int tmp = Integer.MAX_VALUE;
        Map<Integer, Integer> map = new HashMap<>();
        for (int i = 0; i < n / 2; ++i) {
            map.put(Math.abs(nums[i] - nums[n - i - 1]), map.getOrDefault(Math.abs(nums[i] - nums[n - i - 1]), 0) + 1);
            if (map.get(Math.abs(nums[i] - nums[n - i - 1])) >= cnt) {
                cnt = map.get(Math.abs(nums[i] - nums[n - i - 1]));
            }
        }
        for (Map.Entry<Integer, Integer> entry : map.entrySet()) {
            if (entry.getValue() == cnt) {
                tmp = Math.min(tmp, entry.getKey());
            }
        }
        //System.out.println(tmp);
        List<Integer> idx = new ArrayList<>();
        for (int i = 0; i < n / 2; ++i) {
            if (Math.abs(nums[i] - nums[n - i - 1]) != tmp) idx.add(i);
        }
        int ans = 0;
        for (Integer id : idx) {
            int x = n - id - 1;
            int m1 = Math.min(nums[x], nums[id]);
            int m2 = Math.max(nums[x], nums[id]);
            if (m1 + tmp > k && m2 - tmp < 0) ans += 2;
            else ans += 1;
        }

        return ans;
    }


}
