class Solution {
public:
    int minChanges(vector<int>& a, int k) {
           unordered_map<int, vector<int>> b;
    vector<int> c;

    int d = a.size();
    int e = d / 2;

    for (int f = 0; f < e; ++f) {
        int g = a[f];
        int h = a[d - f - 1];

        int i = abs(g - h);
        int j = max({g, h, k - g, k - h});

        b[i].push_back(j);
        c.push_back(j);
    }

    int l = d;
    sort(c.begin(), c.end());

    for (const auto& m : b) {
        int n = m.first;
        int o = lower_bound(c.begin(), c.end(), n) - c.begin();
        int p = o * 2 + (e - o);

        for (int q : m.second) {
            p -= (q < n) ? 2 : 1;
        }

        l = min(l, p);
    }

    return l;

    }
};
