    int min(int a,int b){
        if(a<=b)return a;
        return b;
    }
    int minChanges(vector<int>& nums, int k){

        std::unordered_map<int, int> data;
        std::vector<int> info;
        int n = nums.size();

        for (int i = 0; i < n / 2; ++i) {
            int op = std::abs(nums[i] - nums[n - 1 - i]);
            data[op]++;
            info.push_back(std::max({nums[i], nums[n - 1 - i], k - std::min(nums[i], nums[n - 1 - i])}));
        }

        int output = INT_MAX;
        std::sort(info.begin(), info.end());

        for (int i = 0; i <= k; ++i) {
            auto it = std::lower_bound(info.begin(), info.end(), i);
            int op = it - info.begin();

            while (op - 1 >= 0 && info[op - 1] == i) op--;

            if (i <= k / 2) {
                output = std::min(output, static_cast<int>(n / 2 - data[i]));
            }
            output = std::min(output, 2 * static_cast<int>(op) + static_cast<int>(info.size() - op) - data[i]);
        }

        return output;



    }
};
